import React, { useState, useEffect } from "react";
import { getAllUsers } from "../Functions.js";

function NewTransaction() {
  const [users, setUsers] = useState([]);
  const [formData, setFormData] = useState({});

  const handleChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    console.log("value = " + value);
    setFormData((values) => ({ ...values, [name]: value }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(formData);
  };

  useEffect(() => {
    const getUsers = async () => {
      const data = await getAllUsers();
      console.log(data);
      setUsers(data);
    };
    getUsers();
  }, []);

  return (
    <div className="container">
      <div className="pageHeader">New Transaction</div>
      <form className="border" onSubmit={handleSubmit}>
        <div className="input-group mb-3">
          <div className="input-group-prepend">
            <span className="input-group-text">Client</span>
          </div>
          <select
            className="custom-select"
            onChange={handleChange}
            name="userid"
            value="{user.userid}"
          >
            <option> </option>
            {users.map((user) => (
              <option key={user.userid}>{user.username}</option>
            ))}
          </select>
          <div className="input-group-append"></div>
        </div>

        <div className="input-group mb-3">
          <div className="input-group-prepend">
            <span className="input-group-text">Amount</span>
          </div>
          <input
            type="text"
            className="form-control"
            name="Amount"
            onChange={handleChange}
          />
          <div className="input-group-append">
            <span className="input-group-text">.00</span>
          </div>
        </div>

        <div className="input-group mb-3">
          <div className="input-group-prepend">
            <span className="input-group-text">Rate</span>
          </div>
          <input
            className="form-control"
            type="number"
            min="0"
            name="rate"
            onChange={handleChange}
          />
          <div className="input-group-append">
            <span className="input-group-text">%</span>
          </div>
        </div>

        <div className="input-group mb-3">
          <input
            type="text"
            className="form-control"
            min="1"
            onChange={handleChange}
          />
          <div className="input-group-append">
            <span className="input-group-text">Months</span>
          </div>
        </div>

        <div className="d-flex justify-content-center">
          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </div>
      </form>
    </div>
  );
}

export default NewTransaction;
